const axios = require('axios')

module.exports = async (sock, m, config) => {
  const text = m.message.conversation || ''
  
  if (text.startsWith(config.prefix + 'yt')) {
    const url = text.split(' ')[1]
    const { data } = await axios.get(`https://api.example.com/yt?url=${url}&apikey=${config.apiKeys.youtube}`)
    
    await sock.sendMessage(m.key.remoteJid, {
      video: { url: data.url },
      caption: 'Video YouTube'
    })
  }
}
