module.exports = async (sock, m, config) => {
  const isGroup = m.key.remoteJid.endsWith('@g.us')
  if (!isGroup) return

  // Auto-kick banned words
  const text = m.message.conversation || ''
  const bannedWord = config.bannedWords.find(word => text.includes(word))
  
  if (bannedWord) {
    await sock.groupParticipantsUpdate(
      m.key.remoteJid, 
      [m.participant], 
      'remove'
    )
  }
}
